public class TV extends Product {
	protected String size, screen;
		
	
	public void setTVInfo(String company, String name, String serialNo, 
			String size, String screen) {
		super.setProdictInfo(company, name, serialNo);
		this.size = size;
		this.screen = screen;
	}
	
	@Override
	public String display() {
		return  "제조 회사 : "+super.company+"\n"
				+ " 제품명 : "+super.name+" \n "
				+ "고유번호 :"+super.serialNo+" \n "
				+ "인치 :"+ this.size +"\n "
				+ "화면 :"+this.screen;
		
		
		
	}
}